import React, { useState } from 'react';
import {
  Container,
  Typography,
  Paper,
  Button,
  TextField,
  Box,
  CircularProgress,
  Alert,
  Stepper,
  Step,
  StepLabel,
  Card,
  CardContent,
  Chip,
} from '@mui/material';
import { CloudUpload as UploadIcon } from '@mui/icons-material';
import api from '../config/api';
import { AnalysisResult } from '../types/api';

const Analysis: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [code, setCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [result, setResult] = useState<AnalysisResult | null>(null);
  const [activeStep, setActiveStep] = useState(0);
  const [reviewComment, setReviewComment] = useState('');
  const [llmAnalysis, setLlmAnalysis] = useState<any>(null);

  const steps = ['Code Upload', 'Initial Analysis', 'Review', 'LLM Processing', 'Data Dictionary'];

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (files && files[0]) {
      const selectedFile = files[0];
      const validTypes = ['.py', '.js', '.ts', '.tsx', '.jsx'];
      const fileExtension = selectedFile.name.substring(selectedFile.name.lastIndexOf('.')).toLowerCase();

      if (!validTypes.includes(fileExtension)) {
        setError('Invalid file type. Please upload a Python or JavaScript/TypeScript file.');
        return;
      }

      setFile(selectedFile);
      setError(null);
    }
  };

  const handleAnalyze = async () => {
    if (!file && !code) {
      setError('Please upload a file or enter code to analyze.');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      let formData = new FormData();
      if (file) {
        formData.append('file', file);
      } else {
        formData.append('code', code);
      }

      const response = await api.post<AnalysisResult>('/api/analysis/analyze', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setResult(response.data);
      setActiveStep(1);
    } catch (err: any) {
      setError(err.response?.data?.message || 'Analysis failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleReviewSubmit = async () => {
    try {
      const response = await api.post('/api/review/submit', {
        analysisId: result?.id,
        comment: reviewComment,
        status: 'approved'
      });

      if (response.data.success) {
        setActiveStep(3);
        // Trigger LLM analysis
        await processWithLLM();
      }
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to submit review.');
    }
  };

  const processWithLLM = async () => {
    try {
      const response = await api.post('/api/llm/analyze', {
        analysisId: result?.id,
        review: reviewComment
      });

      setLlmAnalysis(response.data);
      setActiveStep(4);
    } catch (err: any) {
      setError(err.response?.data?.message || 'LLM analysis failed.');
    }
  };

  const renderStep = () => {
    switch (activeStep) {
      case 0:
        return (
          <Box>
            <input
              type="file"
              accept=".py,.js,.ts,.tsx,.jsx"
              style={{ display: 'none' }}
              id="code-file-input"
              onChange={handleFileChange}
            />
            <label htmlFor="code-file-input">
              <Button
                variant="contained"
                component="span"
                startIcon={<UploadIcon />}
                sx={{ mb: 2 }}
              >
                Upload Code File
              </Button>
            </label>
            {file && <Typography>Selected file: {file.name}</Typography>}
            <Typography sx={{ mt: 2, mb: 1 }}>Or paste your code:</Typography>
            <TextField
              multiline
              rows={10}
              fullWidth
              value={code}
              onChange={(e) => setCode(e.target.value)}
              placeholder="Paste your code here..."
              variant="outlined"
              sx={{ mb: 2 }}
            />
            <Button
              variant="contained"
              onClick={handleAnalyze}
              disabled={loading || (!file && !code)}
            >
              {loading ? <CircularProgress size={24} /> : 'Analyze'}
            </Button>
          </Box>
        );

      case 1:
        return (
          <Box>
            <Typography variant="h6" gutterBottom>Initial Analysis Results</Typography>
            {result && (
              <>
                <Card sx={{ mb: 2 }}>
                  <CardContent>
                    <Typography variant="h6">Detected Tables</Typography>
                    {result.tables.map((table, index) => (
                      <Box key={index} sx={{ mb: 2 }}>
                        <Typography variant="subtitle1">{table.name}</Typography>
                        {table.fields.map((field, fieldIndex) => (
                          <Chip
                            key={fieldIndex}
                            label={`${field.name}: ${field.type}`}
                            sx={{ m: 0.5 }}
                          />
                        ))}
                      </Box>
                    ))}
                  </CardContent>
                </Card>
                <Button variant="contained" onClick={() => setActiveStep(2)}>
                  Proceed to Review
                </Button>
              </>
            )}
          </Box>
        );

      case 2:
        return (
          <Box>
            <Typography variant="h6" gutterBottom>Review Analysis</Typography>
            <TextField
              multiline
              rows={4}
              fullWidth
              value={reviewComment}
              onChange={(e) => setReviewComment(e.target.value)}
              placeholder="Add your review comments..."
              variant="outlined"
              sx={{ mb: 2 }}
            />
            <Button
              variant="contained"
              onClick={handleReviewSubmit}
              disabled={!reviewComment}
            >
              Submit Review
            </Button>
          </Box>
        );

      case 3:
        return (
          <Box>
            <Typography variant="h6" gutterBottom>LLM Processing</Typography>
            <CircularProgress />
            <Typography>Processing with LLM...</Typography>
          </Box>
        );

      case 4:
        return (
          <Box>
            <Typography variant="h6" gutterBottom>Final Data Dictionary</Typography>
            {llmAnalysis && (
              <Card>
                <CardContent>
                  <Typography variant="h6">Generated Data Dictionary</Typography>
                  <pre>{JSON.stringify(llmAnalysis, null, 2)}</pre>
                </CardContent>
              </Card>
            )}
          </Box>
        );

      default:
        return null;
    }
  };

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} sx={{ p: 4, mt: 4 }}>
        <Typography variant="h4" gutterBottom>
          Code Analysis
        </Typography>

        <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {renderStep()}
      </Paper>
    </Container>
  );
};

export default Analysis;
